it('calls the callback if the socket is closed prematurely', done => {
  const called = [];
  const wss = new WebSocket.Server({
    perMessageDeflate: true,
    port: 0
  }, () => {
    const ws = new WebSocket(`ws://localhost:${wss.address().port}`, {
      perMessageDeflate: {
        threshold: 0
      }
    });
    ws.on('open', () => {
      ws.send('foo');
      ws.send('bar', err => {
        called.push(1);
        assert.strictEqual(ws.readyState, WebSocket.CLOSING);
        assert.ok(err instanceof Error);
        assert.strictEqual(err.message, 'The socket was closed while data was being compressed');
      });
      ws.send('baz');
      ws.send('qux', err => {
        called.push(2);
        assert.strictEqual(ws.readyState, WebSocket.CLOSING);
        assert.ok(err instanceof Error);
        assert.strictEqual(err.message, 'The socket was closed while data was being compressed');
      });
    });
  });
  wss.on('connection', ws => {
    ws.on('close', () => {
      assert.deepStrictEqual(called, [1, 2]);
      wss.close(done);
    });
    ws._socket.end();
  });
});

