        clientCloseEventEmitted = true;
        if (serverClientCloseEventEmitted) wss.close(done);
      });
    });
    ws.on('open', () => {
      // Write an invalid frame in both directions and change the
      // `readyState` to `WebSocket.CLOSING`.
      const chunk = Buffer.from([0x85, 0x00]);
