            assume(false).true('TransportStream was improperly written to');
        }
    });

    var logger = winston.createLogger({
        transports: [neverLogTo],
        silent: true
    });

    logger.log({
        level: 'info',
        message: 'This should be ignored'
    });

    setImmediate(() => done());
});

