it('Can load React', async () => {
  const html = await fixture.readFile('/index.html');
  const $ = cheerio.load(html); // test 1: basic component renders

  expect($('#react-static').text()).to.equal('Hello static!'); // test 2: no reactroot

  expect($('#react-static').attr('data-reactroot')).to.equal(undefined); // test 3: Can use function components

  expect($('#arrow-fn-component')).to.have.lengthOf(1); // test 4: Can use spread for components

  expect($('#component-spread-props')).to.have.lengthOf(1); // test 5: spread props renders

  expect($('#component-spread-props').text(), 'Hello world!'); // test 6: Can use TS components

  expect($('.ts-component')).to.have.lengthOf(1); // test 7: Can use Pure components

  expect($('#pure')).to.have.lengthOf(1); // test 8: Check number of islands

  expect($('astro-root[uid]')).to.have.lengthOf(5); // test 9: Check island deduplication

  const uniqueRootUIDs = new Set($('astro-root').map((i, el) => $(el).attr('uid')));
  expect(uniqueRootUIDs.size).to.equal(4);
});

