it('resolves CSS in src/styles', async () => {
  expect(bundledCSS, 'includes used component classes').to.match(/\.bg-purple-600{/); // tests a random tailwind class that isn't used on the page

  expect(bundledCSS, 'purges unused classes').not.to.match(/\.bg-blue-600{/); // tailwind escapes colons, `lg:py-3` compiles to `lg\:py-3`

  expect(bundledCSS, 'includes responsive classes').to.match(/\.lg\\:py-3{/); // tailwind escapes brackets, `font-[900]` compiles to `font-\[900\]`

  expect(bundledCSS, 'supports arbitrary value classes').to.match(/\.font-\\\[900\\\]{font-weight:900}/);
});

