it('<style scoped>', async () => {
  const el = $('#vue-scoped'); // find data-v-* attribute (how Vue CSS scoping works)

  const {
    attribs
  } = el.get(0);
  const scopeId = Object.keys(attribs).find(k => k.startsWith('data-v-'));
  expect(scopeId).to.be.ok; // 1. check HTML

  expect(el.attr('class')).to.include('vue-scoped'); // 2. check CSS

  expect(bundledCSS).to.include(`.vue-scoped[${scopeId}]`);
});

